INSERT INTO `t_organization` VALUES ('4', '机关党委', '【处室成员】\r\n\r\n \r\n姓  名\r\n\r\n性别\r\n\r\n职 务\r\n\r\n办公电话\r\n\r\n卢俊波\r\n\r\n男\r\n\r\n专职副书记\r\n\r\n88918355\r\n\r\n梅  捷\r\n\r\n女\r\n\r\n工会主席\r\n\r\n88918295\r\n\r\n刘传忠\r\n\r\n男\r\n\r\n调研员\r\n\r\n机关纪委书记\r\n\r\n88918300\r\n\r\n江建明\r\n\r\n男\r\n\r\n副调研员\r\n\r\n机关纪委副书记\r\n\r\n88918355\r\n\r\n \r\n\r\n \r\n\r\n    【工作职责】\r\n\r\n \r\n\r\n1、负责机关和直属单位的党群工作。', null, '行政单位');
INSERT INTO `t_organization` VALUES ('5', '法规处（统计执法监督局）', '【处室成员】\r\n\r\n \r\n姓  名\r\n\r\n性 别\r\n\r\n职 务\r\n\r\n办公电话\r\n\r\n杨裕光\r\n\r\n男\r\n\r\n处长（局长）\r\n\r\n88918530\r\n\r\n胡友华\r\n\r\n男\r\n\r\n副处长（副局长）\r\n\r\n88918565\r\n\r\n万智勇\r\n\r\n男\r\n\r\n普查中心副主任\r\n\r\n（副局长）\r\n\r\n88918565\r\n\r\n \r\n    【工作职责】\r\n\r\n \r\n\r\n    1、组织实施对全省统计工作的监督检查，预防和查处统计造假、弄虚作假；\r\n\r\n2、组织实施全省性统计执法检查；\r\n\r\n3、受理统计违法举报；\r\n\r\n4、查办、承办、转办、督办统计违法案件；\r\n\r\n5、建立完善统计信用制度；\r\n\r\n6、指导监督市县统计执法检查工作；\r\n\r\n7、起草地方性统计法规规章草案，组织开展统计普法宣传；\r\n\r\n8、负责统计行政许可项目的实施与监督；\r\n\r\n9、办理统计行政复议、应诉和其他法律性事务；\r\n\r\n10、做好改革与法治的衔接工作，承担统计改革与统计法治相关的任务。', null, '行政单位');
INSERT INTO `t_organization` VALUES ('6', '国民经济综合统计处', '【处室成员】   \r\n\r\n \r\n\r\n姓  名\r\n\r\n性 别\r\n\r\n职 务\r\n\r\n办公电话\r\n\r\n康冬明\r\n\r\n男\r\n\r\n处  长\r\n\r\n88918398\r\n\r\n杨幸丽\r\n\r\n女\r\n\r\n副处长\r\n\r\n88918320\r\n\r\n刘卫红\r\n\r\n男\r\n\r\n副处长\r\n\r\n88918321\r\n\r\n程新元\r\n\r\n男\r\n\r\n二级调研员\r\n\r\n88918321\r\n\r\n杨金华\r\n\r\n男\r\n\r\n二级调研员\r\n\r\n88918321\r\n\r\n张雪梅\r\n\r\n女\r\n\r\n四级调研员\r\n\r\n88918320\r\n\r\n \r\n\r\n     【工作职责】\r\n\r\n \r\n\r\n1、对全省经济运行状况进行统计监测、预警，分析研究经济社会重大问题，提出宏观调控咨询建议；\r\n\r\n2、整理、编辑并提供综合性统计资料，管理统计数据；\r\n\r\n3、负责统计数据发布和统计新闻宣传，承办统计新闻发布会。', null, '行政单位');
INSERT INTO `t_organization` VALUES ('7', '普查中心', '【处室成员】 \r\n\r\n \r\n\r\n姓  名\r\n\r\n性 别\r\n\r\n职 务\r\n\r\n办公电话\r\n\r\n黄正坤\r\n\r\n男\r\n\r\n主  任\r\n\r\n88918332\r\n\r\n彭  虹\r\n\r\n男\r\n\r\n副主任\r\n\r\n88918333\r\n\r\n宋世平\r\n\r\n男\r\n\r\n调研员\r\n\r\n88918333\r\n\r\n邓新欣\r\n\r\n男\r\n\r\n副调研员\r\n\r\n88918331\r\n\r\n \r\n\r\n \r\n\r\n \r\n\r\n \r\n\r\n \r\n\r\n【工作职责】\r\n\r\n1、负责全省基本单位普查、第三产业普查、人口普查、工业普查、农业普查及投入产出调查的具体组织实施工作；\r\n\r\n2、负责研究和提出全省普查方案，研究解决普查专业技术方面的各类问题；\r\n\r\n3、负责各种普查和专项统计的数据汇总、资料分析和管理，提供咨询服务；\r\n\r\n4、负责指导与各种普查和专项统计调查相关的统计基础工作和人员培训。', null, '事业单位');
