INSERT INTO `t_organization` VALUES ('9', '计算中心', ' 【处室成员】 \r\n\r\n \r\n姓  名\r\n\r\n性 别\r\n\r\n职  务\r\n\r\n办公电话\r\n\r\n皮人学\r\n\r\n男\r\n\r\n主  任\r\n\r\n88918528\r\n\r\n宗  平\r\n\r\n男\r\n\r\n副主任\r\n\r\n88918527\r\n\r\n林晓倩\r\n\r\n女\r\n\r\n副主任\r\n\r\n88918527\r\n\r\n曾轶雄\r\n\r\n男\r\n\r\n副主任\r\n\r\n88918323\r\n\r\n \r\n   \r\n\r\n    【工作职责】\r\n\r\n \r\n\r\n　　1、为统计数据质量的控制、评估，提供理论指导；\r\n\r\n　　2、承担企业一套表日常运行维护以及大型普查的数据处理、资料开发任务；\r\n\r\n　　3、负责计算机设备及耗材的采购、管理和维护；\r\n\r\n　　4、负责广域网、局域网的建设和维护；\r\n\r\n　　5、建立健全全省统计信息自动化系统；\r\n\r\n　　6、负责全省统计网络信息安全等相关工作。', null, '事业单位');
